create or replace procedure inserirPaises(in par_nome varchar(32), in par_sigla char(4)) language plpgsql as $$ declare  idPais integer := (select id from pais p where p.ativo = true and p.nome_pais = par_nome and p.sigla_pais = par_sigla limit 1); begin if(idPais is null) then begin insert into pais (id, nome_pais, sigla_pais, ativo) values (nextval('hibernate_sequence'), par_nome, par_sigla, true); end; end if; end; $$;
